import React, { Component } from "react";
import Login_UI from "./Login_UI";
import SignUp_UI from "./SignUp_UI";

class LoginSignUp extends Component {
  constructor(props) {
    super();
    this.state = props;
    this.toggle_page = false;
    this.handleLogin = this.handleLogin.bind(this)
    this.handleSignUp = this.handleSignUp.bind(this)
  }

  handleLogin(e) {
    // write Login Code Here
    this.state.setLoggenIn(true)
  }

  handleSignUp(e) {
    // write Signup Code Here
    this.state.setLoggenIn(true)
  }

  render() {
    return (
      <div className="LoginSignUp">
        <div className="LoginSignUp_inner">
          {((this.toggleLoginSigupState == true) ? (
            <Login_UI
              data={this.state}
              attemptLogin={this.handleLogin}
            />)
            :
            (
              <SignUp_UI
                data={this.state}
                attemptSignup={this.handleSignUp}
              />

            ))
          }

          <button onClick={()=>this.toggle_page=!this.toggle_page}>Login</button>
        </div>
      </div>
    )
  }
}
export default LoginSignUp;
