import React from 'react';
import './App.css';
import { getApiReference } from './Api/Api';
import LoadingComponent from './Component/Loading/LoadingComponent';
import LoginSignupComponent from './Component/LoginSignup/LoginSignupComponent';
import CrackOffCampus from './Component/CrackOffCampus-Jobs/CrackOffCampus/CrackOffCampus';
//redux

import { useDispatch, useSelector } from "react-redux";
import { setLoggedIn, setUserDetatil, setInformation_announcementDetails, setJobsData } from "../src/redux/actions/actions";
//


const apiLoadingText = { "text": "Crack of Campus" };


function App() {
    const dispatch = useDispatch();
    const [data, setData] = React.useState(null);
    const [isLoggedIn, setLoggenIn] = React.useState(false);

    // ApiCall from Reference

    React.useEffect(() => {
        getApiReference().then(result => {
            setTimeout(() => {
                setData(result.data)
            }, 1500)
        });
    }, []);

    setDataInStore(data)
    function setDataInStore(data){
        if(data==null) return
        //{dispatch(setUserDetatil(data.userDetails))}
       // {dispatch(setInformation_announcementDetails(data.information_announcement))}
       // {dispatch(setJobsData(data.data)) }
    }

    //Rendering Root Component

    return (
        <div className="App" > {
            (data == null) ? (< LoadingComponent {...apiLoadingText} />
            ) :
                (
                    //Data Fetched Sussessfully
                    // checking for Login
                    (!isLoggedIn) ?
                        (
                            // Need To Login
                            <LoginSignupComponent setLoggenIn={setLoggenIn} />
                        ) :
                        (
                            // LoggedIn successfully or Already LoggedIn
                            // After Login : CrackoffCampus Is Main Component
                            <CrackOffCampus {...data} setLoggenIn={setLoggenIn} />
                        )

                )
        }
        </div>
    )

}

export default App;
