import React, { Component } from "react";
import Login_UI from "./Login_UI";
import SignUp_UI from "./SignUp_UI";

class LoginSignUp extends Component {
  constructor(props) {
    super();
    this.state = {
      props:props,
      loginDetails:{username:"",password:""},
      signupDetails:{username:"",email:"",password:""},
      isLoginPageVisible : false
    };
    this.handleLogin = this.handleLogin.bind(this)
    this.handleSignUp = this.handleSignUp.bind(this)
  }

  handleLogin(e) {
    // write Login Code Here
    this.state.props.setLoggenIn(true)
    console.log(this.toggle_page)
  }

  handleSignUp(e) {
    // write Signup Code Here
    this.state.props.setLoggenIn(true)
    console.log(this.toggle_page)
  }

  render() {
    return (
      <div className="LoginSignUp">
        <div className="LoginSignUp_inner">
          {((this.state.isLoginPageVisible == true) ? (
            <Login_UI
              data={this.state.loginDetails}
              attemptLogin={this.handleLogin}
            />)
            :
            (
              <SignUp_UI
                data={this.state.signupDetails}
                attemptSignup={this.handleSignUp}
              />

            ))
          }

          <button onClick={()=>{
            this.state.isLoginPageVisible=!this.state.isLoginPageVisible}}>Login</button>
        </div>
      </div>
    )
  }
}
export default LoginSignUp;
