import { getApiReference } from '../../Api/Api';

export const increment =(num)=>{
    return {
      type:'INCREMENT',
      payload: num
    };
 }
 export const decrement =()=>{
    return {type:'DECREMENT'};
 }
 export const setLoggedIn=()=>{
   return {type:'LOGIN'};
 }
 export const setLoginPageVisible=()=>{
   return {type:'LOGIN_VISIBLE'};
 }
 export const setUserDetatil=(data)=>{
  return {type:'USER_DETAIL',
  payload:data};
}
export const setInformation_announcementDetails=(data)=>{
  return {type:'INFORMATION_ANNOUNCEMENT',
  payload:data};
}
export const setJobsData=(data)=>{
  return {type:'JOBS_DATA',
  payload:data};
} 

export const fetchData= ()=>{
  return (dispatch)=>{
    getApiReference()
    .then(result => {
          dispatch(setUserDetatil(result.data.userDetails))
          dispatch(setInformation_announcementDetails(result.data.information_announcement))
          dispatch(setJobsData(result.data.data)) 
    })
  }
}
