import React from 'react';import './App.css';
import { getApiReference } from './Api/Api';
import Loading from './Component/Loading/Loading';
import LoginSignUp from './Component/LoginSignUp/LoginSignUp';
import CrackOffCampus from './Component/CrackOffCampus/CrackOffCampus';
const apiLoadingText={"text":"Featching Data..."};
const baseUrl='https://raw.githubusercontent.com/weepiccoders/Temp-Api/main/sample.json';

function App() {

  // ApiCall
  const [data,setData]=React.useState(null);
   const [isLoggedIn, setLoggenIn]=React.useState(true);
 // React.useEffect(()=>{
  //     getApiReference().then(result=>setData(result));
  //},{});

  React.useEffect(()=>{
    fetch(baseUrl)
    .then((response)=>{
      return response.json();
    })
    .then((data)=>{
      setData(data);
    });
   },[])

  // Rendering Root Component

  return(
    <div className="App">
      {(data==null)?(<Loading {...apiLoadingText}/>)
      :
      (
      //Data Fetched Sussessfully
          // checking for Login
          (!isLoggedIn)?
          (
           // Need To Login
          <LoginSignUp />
          )
          :
          (

            // LoggedIn successfully or Already LoggedIn
            <CrackOffCampus {...data}/>
          )

      )}
    </div>
  )

}

export default App;
