import logo from './logo.svg';
import './App.css';
import { getApiReference } from './Api/Api';
import React from 'react';
import Loading from './Loading/Loading';
const apiLoadingText="Featching Data";
function App() {

  // ApiCall
  const [data,setData]=React.useState(null);
  React.useEffect(()=>{
       getApiReference().then(result=>setData(result.data));
  },[]);

  //// Rendering Root Component

  return(
    <div className="App">
      {(data==null)?(
         <Loading apiLoadingText/>
      )
      :
      (
        <div className="App"></div>
      )}
    </div>
  )

}

export default App;
